import React,{useEffect,useState} from "react";
import logo from './logo.svg';
import './App.css';
import Word from "./components/Word/Word";

function App() {
  const [pressed,setPressed]=useState([]);
  const [correctLetters,setCorrectLetters]=useState([]);
  const [word,setWord]=useState("");
  const keysRegex=/^[A-z]$/;
  useEffect(()=>{
    setWord("aligator");
  },[])
  useEffect(()=>{
    window.addEventListener("keydown",(e)=>{
      const pressedKey=e.key;
      if(keysRegex.test(pressedKey)){
        if(!pressed.includes(pressedKey) && !correctLetters.includes(pressedKey)){
          if(word.indexOf(pressedKey)!=-1){
            setCorrectLetters([...setCorrectLetters,pressedKey]);
          }
          else{
            setPressed([...pressed,pressedKey]);
          }
        }
        else{
          console.log("Vec je bilo");
        }
      }
      else{
        console.log("Nije slovo")
      }
    })
  },[pressed])


  return (
    <div className="App">
      <header className="App-header">
       <Word wordToRender={word} guessLetters={correctLetters} />
       {console.log(pressed,correctLetters)}
      </header>
    </div>
  );
}

export default App;
