import React,{useEffect,useState} from "react";
import logo from './logo.svg';
import './App.css';
import Word from "./components/Word/Word";

function App() {
  const [pressed,setPressed]=useState([]);
  const [correctLetters,setCorrectLetters]=useState([]);
  const [wrongLetters,setWrongLetters]=useState([]);
  const [word,setWord]=useState("");
  const keysRegex=/^[A-z]$/;
  useEffect(()=>{
    setWord("aligator");
  },[])
  useEffect(()=>{
    window.addEventListener("keydown",(e)=>{
      const pressedKey=e.key;

      if(keysRegex.test(pressedKey)){
        //console.log(pressedKey);
        if(!pressed.includes(pressedKey)){
            setWrongLetters([...wrongLetters,pressedKey]);
            setPressed([...pressed,pressedKey])
        }
        else{
          console.log("Vec ima");
        }
      }
      else{
        console.log("Nije slovo");
      }
    })
  },[pressed])


  return (
    <div className="App">
      <header className="App-header">
       <Word wordToRender={word} guessLetters={correctLetters} />
       {console.log(wrongLetters,correctLetters)}
       {wrongLetters.map(letter=><p>{letter}</p>)}
      </header>
    </div>
  );
}

export default App;
