import React,{useEffect,useState} from "react";
import logo from './logo.svg';
import './App.css';
import Word from "./components/Word/Word";

function App() {
  const [pressed,setPressed]=useState([]);
  const [wrongKeys,setWrongKeys]=useState([]);
  const [word,setWord]=useState("");
  const keysRegex=/^[A-z]{1,}$/;

  useEffect(()=>{
    setWord("aligator");
  },[])
  useEffect(()=>{
    document.addEventListener("keydown",(e)=>{
      const pressedKey=e.key;
      if(keysRegex.test(pressedKey)){
        if(!pressed.includes(pressedKey)){
          setPressed([...pressed,pressedKey])
        }
        else{
          console.log("Vec ima");
        }
      }
      else{
        console.log("Nije slovo");
      }


    })
  },[pressed])


  return (
    <div className="App">
      <header className="App-header">
       <Word wordToRender={word} />
       {console.log(pressed)}
      </header>
    </div>
  );
}

export default App;
